package sonar;import org.sonar.plugins.java.api.IssuableSubscriptionVisitor;import org.sonar.plugins.java.api.JavaCheck;import org.sonar.plugins.java.api.tree.MethodTree;import org.sonar.plugins.java.api.tree.Tree;import java.util.Collections;import java.util.List;public class MyCustomJavaRule extends IssuableSubscriptionVisitor implements JavaCheck {    private static final String RULE_KEY = "AvoidSystemOutPrintln";    @Override    public List<Tree.Kind> nodesToVisit() {        return Collections.singletonList(Tree.Kind.METHOD);    }    @Override    public void visitNode(Tree tree) {        MethodTree methodTree = (MethodTree) tree;        String methodName = methodTree.simpleName().name();        if (methodName.equals("println") || methodName.equals("print")) {            reportIssue(methodTree, "Avoid using System.out.println, use a proper logging framework.");        }    }}