package sonar;import org.sonar.api.server.rule.RulesDefinition;public class MyCustomRulesDefinition implements RulesDefinition {    @Override    public void define(Context context) {        NewRepository repository = context.createRepository("custom-java-rules", "java")                .setName("Custom Java Rules");        repository.createRule("AvoidSystemOutPrintln")                .setName("Avoid System.out.println")                .setHtmlDescription("Use a proper logging framework instead of System.out.println.")                .setSeverity("MAJOR");        repository.done();    }}